body {
  font-family: "Poppins", sans-serif;
  color: #040000;
  background-color: #666366;
}

.layout_padding {
  padding-top: 120px;
  padding-bottom: 120px;
}

.layout_padding2 {
  padding: 45px 0;
}

.layout_padding2-top {
  padding-top: 45px;
}

.layout_padding2-bottom {
  padding-bottom: 45px;
}

.layout_padding-top {
  padding-top: 120px;
}

.layout_padding-bottom {
  padding-bottom: 120px;
}

.heading_container {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-orient: vertical;
  -webkit-box-direction: normal;
      -ms-flex-direction: column;
          flex-direction: column;
  -webkit-box-align: start;
      -ms-flex-align: start;
          align-items: flex-start;
}

.heading_container h2 {
  font-weight: bold;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-align: center;
      -ms-flex-align: center;
          align-items: center;
  text-transform: uppercase;
  color: #010201;
}

/*header section*/
.hero_area {
  height: 100vh;
  background-image: url(../images/hero-bg.jpg);
  background-repeat: no-repeat;
  background-size: cover;
  position: relative;
}

.hero_area::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(transparent), to(#9b8f9a));
  background: linear-gradient(to bottom, transparent, transparent, #9b8f9a);
}

.sub_page .hero_area {
  height: auto;
}

.sub_page .class_section .owl-carousel .owl-nav {
  top: 55%;
}

.header_section {
  background-color: #ffffff;
  position: relative;
  -webkit-clip-path: polygon(50% 0%, 100% 0, 100% 100%, 28.5% 100%, 26.5% 86%, 0 84%, 0 0);
          clip-path: polygon(50% 0%, 100% 0, 100% 100%, 28.5% 100%, 26.5% 86%, 0 84%, 0 0);
}

.header_section::before {
  content: "";
  position: absolute;
  top: 0;
  right: 0;
  width: 62%;
  height: 45px;
  background: #080203;
  z-index: 1;
  -webkit-clip-path: polygon(50% 0%, 100% 0, 100% 100%, 8% 100%, 0 0);
          clip-path: polygon(50% 0%, 100% 0, 100% 100%, 8% 100%, 0 0);
}

.header_section .nav_container {
  margin: 0 auto;
}

.menu_width {
  width: 100%;
}

a,
a:hover,
a:focus {
  text-decoration: none;
}

a:hover,
a:focus {
  color: initial;
}

.btn,
.btn:focus {
  outline: none !important;
  -webkit-box-shadow: none;
          box-shadow: none;
}

.header_nav {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-pack: justify;
      -ms-flex-pack: justify;
          justify-content: space-between;
}

.main_nav {
  position: relative;
  z-index: 9;
}

.main_nav .top_nav ul {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  justify-content: flex-end; /* จัดตำแหน่งให้ชิดขวา */
  margin: 0;
  padding: 0; /* ลบ padding ที่อาจทำให้ไม่ชิดขวา */
}

.main_nav .top_nav ul li {
  list-style-type: none;
}

.main_nav .top_nav ul li a {
  display: inline-block;
  color: #ffffff;
  margin: 10px 0 10px 35px;
}

.main_nav .top_nav ul li a img {
  max-width: 25px;
  max-height: 25px;
  margin-right: 15px;
}


.custom_nav-container.navbar-expand-lg .navbar-nav .nav-item .nav-link {
  padding: 10px 20px;
  color: #bb3535;
  text-align: center;
  text-transform: uppercase;
}

a,
a:hover,
a:focus {
  text-decoration: none;
}

a:hover,
a:focus {
  color: initial;
}

.btn,
.btn:focus {
  outline: none !important;
  -webkit-box-shadow: none;
          box-shadow: none;
}

.custom_nav-container .nav_search-btn {
  background-image: url(../images/search-icon.png);
  background-size: 22px;
  background-repeat: no-repeat;
  background-position-y: 7px;
  width: 35px;
  height: 35px;
  padding: 0;
  border: none;
}

.navbar-brand {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-align: center;
      -ms-flex-align: center;
          align-items: center;
}

.navbar-brand img {
  width: 125px;
  margin-right: 5px;
}

.navbar-brand.brand_mobile {
  display: none;
}

.custom_nav-container {
  z-index: 99999;
}

.custom_nav-container .navbar-toggler {
  outline: none;
}

.custom_nav-container .navbar-toggler .navbar-toggler-icon {
  background-image: url(../images/menu.png);
  background-size: 55px;
}

/*end header section*/
/* slider section */
.slider_section {
  height: calc(100% - 80px);
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-pack: center;
      -ms-flex-pack: center;
          justify-content: center;
  -webkit-box-align: center;
      -ms-flex-align: center;
          align-items: center;
}

.slider_section #carouselExampleIndicators {
  width: 100%;
}

.slider_section .row .col-md-10 {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
}

.slider_section .slider_heading {
  -webkit-transform: rotate(270.5deg) scaleX(-1);
          transform: rotate(270.5deg) scaleX(-1);
  position: absolute;
  left: -150px;
  top: -25px;
}

.slider_section .slider_heading h2 {
  display: -webkit-box;
  display: -ms-flexbox;
